import React from 'react';
import { Card, CardBody, CardTitle, CardText } from 'reactstrap';
import { Link, useParams } from 'react-router-dom';
import { connect } from 'react-redux';
import { deleteRecipe } from '../actions/index.js';

const DetailedCard = (props) => {
    const { id } = useParams();
    console.log(id);
    const relRecipe = props.recipes.filter((recipe) => recipe.id === id);

    return (
        <div className="recipeCard">
            <Link to="/dashboard">
                <button className="navButton">Return to Dashboard</button>
            </Link>
            <Card className="miniCard">
                <CardBody>
                    <CardTitle tag="h3">{relRecipe[0].title}</CardTitle>
                    <CardTitle tag="h3">{relRecipe[0].id}</CardTitle>
                </CardBody>
                <CardBody>
                    <CardText>{`Recipe By: ${relRecipe[0].source}`}Recipe By: Mom</CardText>
                    <CardText>{relRecipe[0].category}</CardText>
                </CardBody>
            </Card>
            <Card className="miniCard">
                <CardBody>
                    <CardTitle tag="h3">Ingredients</CardTitle>
                </CardBody>
                <CardBody>
                    {
                        relRecipe[0].ingredients.map((ingredient) => {
                            return <CardText>{ingredient}</CardText>
                        })
                    }
                </CardBody>
            </Card>
            <Card className="miniCard">
                <CardBody>
                    <CardTitle tag="h3">Instructions</CardTitle>
                </CardBody>
                <CardBody>
                    {
                        relRecipe[0].instructions.map((instruction) => {
                            return <CardText>{instruction}</CardText>
                        })
                    }
                </CardBody>
            </Card>
            <div className="editRecipe">
                <button>Edit Recipe</button>
                <button>Delete Recipe</button>
            </div>
        </div>
    )
};

const mapStateToProps = (state) => {
    return {
        recipes: state.recipes
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        deleteRecipe: (id) => dispatch(deleteRecipe(id))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(DetailedCard);
