import React from 'react';
import MiniCard from './minirecipecard.js';
import { Container, Button } from 'reactstrap';
import { useHistory, Link } from 'react-router-dom';
import { connect } from 'react-redux';

const Dashboard = (props) => {

    const history = useHistory();

    const logout = (evt) => {
        evt.preventDefault();
        localStorage.removeItem('authToken');
        history.push('/')
    }
    return (
        <div>
            <div className="navigation">
                <button className="navButton" onClick={logout}>Logout</button>
                <Link to="/form">
                    <Button className="navButton">Enter New Recipe</Button>
                </Link>
            </div>
            <Container className="card-container" fluid={true}>
                {props.recipes && props.recipes.map((recipe, index) => {
                    console.log(recipe);
                    return <MiniCard key={index} recipe={recipe} />
                })}
            </Container>

        </div>
    )
}

const mapStateToProps = (state) => {
    return {
        recipes: state.recipes,
    }
}

export default connect(mapStateToProps)(Dashboard);
