import React, { useEffect } from 'react';
import MiniCard from './minirecipecard.js';
import { Container } from 'reactstrap';
import { useHistory } from 'react-router-dom';
import { connect } from 'react-redux';
import Search from "../Components/search.js";
import axiosWithAuth from '../Utils/axiosWithAuth';
import { fetchRecipes } from '../actions/index.js';

const Dashboard = (props) => {

    const { fetchRecipes, recipes, loading } = props;

    //This helps us decode the token
    let user = localStorage.getItem('authToken');
    let base64Url = user.split('.')[1];
    const userId = JSON.parse((window.atob(base64Url)));
    console.log(userId);

    const history = useHistory();

    const logout = (evt) => {
        evt.preventDefault();
        localStorage.removeItem('authToken');
        history.push('/')
    }

    const navigateFunction = (evt) => {
        history.push('/form');
    }

    useEffect(() => {
        ///api/users/2/recipes - User Specific Recipes
        fetchRecipes(`https://recipeslambda.herokuapp.com/api/recipes`)
    }, [userId.userID, fetchRecipes])

    return (
        <div className="dashboard">
            <div className="navigation">
                <button className="navButton" onClick={navigateFunction}>Enter New Recipe</button>
                <Search />
                <button className="navButton" onClick={logout}>Logout</button>
            </div>
            <Container className="card-container" fluid={true}>
                {props.recipes.map((recipe) => {
                    return <MiniCard key={recipe.id} recipe={recipe} />
                })}
            </Container>
        </div>
    )
}

const mapStateToProps = (state) => {
    return {
        loading: state.loading,
        recipes: state.recipes,
        error: state.error,
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        fetchRecipes: () => dispatch(fetchRecipes())
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Dashboard);
