import React, { useState, useEffect } from 'react';
import { Container } from 'reactstrap';
import { useHistory, useParams } from 'react-router-dom';
import { connect } from 'react-redux';

const EditForm = (props) => {
    const history = useHistory();

    const { id } = useParams();
    console.log(id);

    const navigateFunction = (evt) => {
        history.push('/dashboard');
    }

    const [recipe, setRecipe] = useState([]);

    useEffect(() => {
        setRecipe(props.recipes.filter((recipe) => recipe.id === parseInt(id)));
    }, [id, props.recipes])

    const ingredientList = recipe[0].ingredients;
    console.log(ingredientList)

    const categoryList = recipe[0].category
    console.log(categoryList);

    const [form, setForm] = useState({
        title: '',
        source: '',
        ingredients: [...ingredientList],
        category: [...categoryList],
        instructions: ''
    })


    if (recipe.length === 0) {
        return <div>Fetching Recipe Details</div>
    }

    return (
        <Container>
            <button className="navButton" onClick={navigateFunction}>Return to Dashboard</button>
        </Container>
    )
}

const mapStateToProps = (state) => {
    return {
        recipes: state.recipes
    }
}

export default connect(mapStateToProps, null)(EditForm);
