import React from 'react';
import { Card, CardBody, CardTitle, CardText } from 'reactstrap';
import { Link, useParams } from 'react-router-dom';
import { connect } from 'react-redux';
import { deleteRecipe } from '../actions/index.js';

const DetailedCard = (props) => {
    const { id } = useParams();
    console.log(id);
    const relRecipe = props.recipes.filter((recipe) => recipe.id === id);
    let recipe = {
        id: 1,
        title: 'Potato Pie',
        source: 'Grandma',
        category: 'Dessert',
        ingredients: [{ name: 'Beef' }, { name: 'Noodles' }, { name: 'Eggs' }],
        instructions: [{ step: 'Boil Water' }, { step: 'Cook The Noodles' }, { step: 'Boil Eggs' }, { step: 'Mix Noodles, Eggs and Beef' }]
    }

    return (
        <div className="recipeCard">
            {console.log(relRecipe)};
            <Link to="/dashboard">
                <button className="navButton">Return to Dashboard</button>
            </Link>
            <Card className="miniCard">
                <CardBody>
                    <CardTitle tag="h3">{recipe.title}</CardTitle>
                    <CardTitle tag="h3">{recipe.id}</CardTitle>
                </CardBody>
                <CardBody>
                    <CardText>{`Recipe By: ${recipe.source}`}Recipe By: Mom</CardText>
                    <CardText>{recipe.category}</CardText>
                </CardBody>
            </Card>
            <Card className="miniCard">
                <CardBody>
                    <CardTitle tag="h3">Ingredients</CardTitle>
                </CardBody>
                <CardBody>
                    {
                        recipe.ingredients.map((ingredient) => {
                            return <CardText>{ingredient.name}</CardText>
                        })
                    }
                </CardBody>
            </Card>
            <Card className="miniCard">
                <CardBody>
                    <CardTitle tag="h3">Instructions</CardTitle>
                </CardBody>
                <CardBody>
                    {
                        recipe.instructions.map((instruction) => {
                            return <CardText>{instruction.step}</CardText>
                        })
                    }
                </CardBody>
            </Card>
            <div className="editRecipe">
                <button>Edit Recipe</button>
                <button>Delete Recipe</button>
            </div>
        </div>
    )
};

const mapStateToProps = (state) => {
    return {
        recipes: state.recipes
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        deleteRecipe: (id) => dispatch(deleteRecipe(id))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(DetailedCard);
