import React from 'react';

import { Button, Col, Form, FormGroup, Label, Input, FormText } from 'reactstrap';

const RecipeForm = (props) => {
    return (
        <div>
            This is the RecipeForm
            <Form>
                <FormGroup row>
                    <Label for="title" sm={2}>Title:</Label>
                    <Input type="text" name="title" id="title" placeholder="Enter Title" />
                </FormGroup>
                <FormGroup row>
                    <Label for="instructions" sm={2}>Instructions</Label>
                    <Col sm={10}>
                        <Input type="textarea" name="text" id="instructions" />
                    </Col>
                </FormGroup>
            </Form>
            <Button>Add Recipe</Button>
        </div>
    )
}

export default RecipeForm;

/*
RecipeForm should have a title, source (ex Grandma), ingredients, instructions, and category (dinner, chicken, dessert, pasta)
*/
