import React, { useEffect } from 'react';
import MiniCard from './minirecipecard.js';
import { Container } from 'reactstrap';
import { useHistory } from 'react-router-dom';
import { connect } from 'react-redux';
import Search from "../Components/search.js";
import axiosWithAuth from '../Utils/axiosWithAuth';

const Dashboard = (props) => {

    let user = localStorage.getItem('authToken');
    let base64Url = user.ca.split('.')[1];
    console.log(JSON.parse(window.atob(base64Url)));

    const history = useHistory();

    const logout = (evt) => {
        evt.preventDefault();
        localStorage.removeItem('authToken');
        history.push('/')
    }

    const navigateFunction = (evt) => {
        history.push('/form');
    }

    useEffect(() => {
        ///api/users/2/recipes - User Specific Recipes
        axiosWithAuth().get('/api/recipes')
            .then((res) => {
                console.log(res.data);
            })
            .catch((err) => {
                console.log(err);
            })
    }, [])

    return (
        <div className="dashboard">
            <div className="navigation">
                <button className="navButton" onClick={navigateFunction}>Enter New Recipe</button>
                <Search />
                <button className="navButton" onClick={logout}>Logout</button>
            </div>
            <Container className="card-container" fluid={true}>
                {props.recipes.map((recipe) => {
                    return <MiniCard key={recipe.id} recipe={recipe} />
                })}
            </Container>
        </div>
    )
}

const mapStateToProps = (state) => {
    return {
        recipes: state.recipes,
    }
}

export default connect(mapStateToProps, null)(Dashboard);
