import React from 'react';
import { Card, CardBody, CardTitle, CardText } from 'reactstrap';
import { Link, useParams } from 'react-router-dom';
import { connect } from 'react-redux';
import { deleteRecipe } from '../actions/index.js';

const DetailedCard = (props) => {
    const { id } = useParams();
    console.log(id);
    const relRecipe = "";

    return (
        <div className="recipeCard">
            {
                relRecipe = props.recipes.filter((recipe) => recipe.id === id)
            }
            <Link to="/dashboard">
                <button className="navButton">Return to Dashboard</button>
            </Link>
            <Card className="miniCard">
                <CardBody>
                    <CardTitle tag="h3">Potato Pie</CardTitle>
                </CardBody>
                <CardBody>
                    <CardText>Recipe By: Mom</CardText>
                    <CardText>Dessert</CardText>
                </CardBody>
            </Card>
            <Card className="miniCard">
                <CardBody>
                    <CardTitle tag="h3">Ingredients</CardTitle>
                </CardBody>
                <CardBody>
                    <CardText>Beef</CardText>
                    <CardText>Noodles</CardText>
                    <CardText>Eggs</CardText>
                </CardBody>
            </Card>
            <Card className="miniCard">
                <CardBody>
                    <CardTitle tag="h3">Instructions</CardTitle>
                </CardBody>
                <CardBody>
                    <CardText>Boil Water</CardText>
                    <CardText>Cook the Noodles</CardText>
                    <CardText>Boil Eggs</CardText>
                    <CardText>Mix Noodles, Eggs and Beef</CardText>
                </CardBody>
            </Card>
            <div className="editRecipe">
                <button>Edit Recipe</button>
                <button>Delete Recipe</button>
            </div>
        </div>
    )
};

const mapStateToProps = (state) => {
    return {
        recipes: state.recipes
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        deleteRecipe: (id) => dispatch(deleteRecipe(id))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(DetailedCard);
